import React from 'react'
import { withRouter } from 'react-router-dom'
import { compose } from 'recompose'

import { SignUpLink } from 'components/SignUp'
import { PasswordForgetLink } from 'components/PasswordForget'
import { withFirebase } from '../../Firebase'
import * as ROUTES from 'constants/routes'

import { Button } from 'antd'

import t from 'assets/languages'

const SignInPage = () => (
  <div className="container--margin">
    <div style={{display: "flex"}}>
      <h1 className="t--capitalize t--bold t--large">{t('welcome')} <br /> {t('back')}!</h1>
      <span style={{width: "100%", textAlign: "right"}}>
      <svg width="125" height="125" viewBox="0 0 125 125" fill="none" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink">
      <rect width="125" height="125" fill="url(#pattern0)"/>
      <defs>
      <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
      <use xlinkHref="#image0" transform="scale(0.001)"/>
      </pattern>
      <image id="image0" width="1000" height="1000" xlinkHref="data:image/png;base64,"/>
      </defs>
      </svg>
      </span>
    </div>
    <SignInForm />
    <PasswordForgetLink />
    <SignUpLink />
  </div>
)

const INITIAL_STATE = {
  email: '',
  password: '',
  error: null,
}

class SignInFormBase extends React.Component {
  constructor(props) {
    super(props)

    this.state = { ...INITIAL_STATE }
  }

  onSubmit = event => {
    if( this.state.password === '' || this.state.email === '' ) {
      return
    }
    const { email, password } = this.state
    console.log('submit')

    this.props.firebase
      .doSignInWithEmailAndPassword(email, password)
      .then(() => {
        this.setState({ ...INITIAL_STATE })
        this.props.history.push(ROUTES.HOME)
      })
      .catch(error => {
        this.setState({ error })
      })

    event.preventDefault()
  }

  onChange = event => {
    this.setState({ [event.target.name]: event.target.value })
  }

  render() {
    const { email, password, error } = this.state

    return (
      <form style={{paddingTop: "8vh", position: "relative"}}>
        <h3 className="t--bold t--capitalize" style={{paddingTop: "2rem"}}>{t('email')}</h3>
        <input
          className="input--no-outline"
          name="email"
          value={email}
          onChange={this.onChange}
          type="text"
          style={{borderWidth: 0, touchAction: "none", width: "100%"}}
        />
        <hr />
        <h3 className="t--bold t--capitalize" style={{paddingTop: "2rem"}}>{t('password')}</h3>
        <input
          className="input--no-outline"
          name="password"
          value={password}
          onChange={this.onChange}
          type="password"
          style={{borderWidth: 0, touchAction: "none", width: "100%"}}
        />
        <hr />
        <Button onClick={this.onSubmit} type="submit" className="b--done t--uppercase t--bold"
                style={{left: "10vw", padding: "1rem", paddingTop: "1rem", paddingBottom: "3rem",
                fontSize: "1.2rem"}}>
          {t('login')}
        </Button>

        {error && <p>{error.message}</p>}
      </form>
    )
  }
}

const SignInForm = compose(
  withRouter,
  withFirebase,
)(SignInFormBase)

export default SignInPage

export { SignInForm }
